package dailypractise;
import java.util.*;
public class area {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		System.out.println("l=");
		int l=sc.nextInt();
		System.out.println("b=");
		int b=sc.nextInt();
		int area=l*b;
		System.out.println("area "+area);
	}

}
